// Brings VG to its default pos where sVertical = sHorizontal = sRotation = TRUE
// 1. y-Axis 2. z-Axis 3. rotation
IF NOT(VG.sVertical) THEN
	VG.mVerticalUp := TRUE;
ELSE
	VG.mVerticalUp := FALSE;
	// reset position counter
	VG.cY := 0;
	IF NOT(VG.sHorizontal) THEN
		VG.mHorizontalBackward := TRUE;
	ELSE
		VG.mHorizontalBackward := FALSE;
		// reset position counter
		VG.cX := 0;
		IF NOT(VG.sRotation) THEN
			VG.mRotationClockwise := TRUE;
		ELSE
			VG.mRotationClockwise := FALSE;
			// reset position counter
			VG.cZ := 0;

			// wait for 0.5 sec
			timer(IN:= TRUE, PT := T#0.5S);
			IF NOT(timer.Q = FALSE AND timer.IN) THEN
				timer(IN:=FALSE);

				// default pos reached
				VG.state := VG_State.AT_DEFAULT_POS;
			END_IF
		END_IF
	END_IF
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
IF VG.state = VG_State.AT_HR THEN
	// this can happen if the batch job before was a single mode batch job. Then VG is already AT HR and is not in ready state
	VG.state := VG_State.TO_HR_TAKE_W;
ELSIF VG.state = VG_State.READY AND (HR.state = HR_State.GET_W AND HR.currW.state <> W_State.NULL) THEN
	// go to HR and take w
	VG.state := VG_State.TO_HR_TAKE_W;
ELSIF VG.state = VG_State.READY AND (NOT(SL.lbWhite) OR NOT(SL.lbRed) OR NOT(SL.lbBlue)) AND SL.currW.state <> W_State.NULL AND (NOT(HR.decidedToTakeANewW) OR HR.currW.state = W_State.NULL) THEN
	// go to SL and take w
	// bend VG pointers to workpiece being picked up
	VG.bIdx := SL.bIdx;
	VG.wIdx := SL.wIdx;
	VG.currW := TC.batchJobQueue.batchJobs[VG.bIdx].workpieces[VG.wIdx];
	VG.state := VG_State.TO_SL_TAKE_W;
ELSIF VG.state = VG_State.TO_SL_TAKE_W_FINISHED THEN
	// reset SL pointers
	SL.bIdx := 0;
	SL.wIdx := 0;
	SL.currW := TC.nullW;
	// at SL, w taken, decide if going to EJECT or HR
	IF VG.currW.format.ejectEnabled THEN
		// go to eject point and eject w
		VG.state := VG_State.TO_EJECT;
	ELSE
		// go to HR and put down w
		VG.state := VG_State.TO_HR_PUT_W;
	END_IF
ELSIF VG.state = VG_State.TO_HR_TAKE_W_FINISHED THEN
	// at HR, w taken, decide if going to FUR or EJECT
	IF VG.currW.format.ejectEnabled AND NOT(VG.currW.format.colorCheckEnabled OR VG.currW.format.furEnabled OR VG.currW.format.sawEnabled) THEN
		// go to eject point and eject w
		VG.state := VG_State.TO_EJECT;
	ELSE
		// go to FUR and put down w
		// but only if FUR-succer is at its default pos
		IF FUR.sSuckerPosRa THEN
			VG.state := VG_State.TO_FUR_PUT_W;
		END_IF
	END_IF
ELSIF VG.state = VG_State.TO_EJECT_FINISHED OR VG.state = VG_State.TO_FUR_PUT_W_FINISHED OR  VG.state = VG_State.TO_HR_PUT_W_FINISHED THEN
	// eject or putting w to FUR finished, back to default position for calibrating axes
	// likewise if VG put down w at HR
	VG.state := VG_State.TO_DEFAULT_POS;
ELSIF VG.state = VG_State.AT_DEFAULT_POS THEN
	// state = ready if VG is at default pos or if VG put down a w at HR --> VG is ready for next w
	VG.state := VG_state.READY;
	// reset own pointers
	VG.bIdx := 0;
	VG.wIdx := 0;
	VG.currW := TC.nullW;
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
CASE step OF
	1:
		// x and z axis: move to FUR
		IF NOT(fin) THEN
			vg_move_to_pos(x := 131, y := 0, z := 137);
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	2:
		// y axis: move to FUR
		IF NOT(fin) THEN
			vg_move_to_pos(x := 131, y := 85, z := 137);
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	3:
		// open ventil to put down w onto waggon
		VG.valve := FALSE;
		step := step + 1;
	4:
		// wait 1 second to get sure w is not succed anymore
		timer(IN:= TRUE, PT:= T#1S);
		IF NOT(timer.Q = FALSE AND timer.IN) THEN
			// reset timer
			timer(IN:= FALSE);
			step := step + 1;
		END_IF
	5:
		// y axis: go up as far as possible
		IF NOT(VG.sVertical) THEN
			VG.mVerticalUp := TRUE;
		ELSE
			VG.mVerticalUp := FALSE;
			// reset y axis counter
			VG.cY := 0;
			// reset step counter
			step := 1;
			// bend FUR-Burner pointers to current workpiece of VG
			FUR.bIdxBurner := VG.bIdx;
			FUR.wIdxBurner := VG.wIdx;
			FUR.currWBurner := TC.batchJobQueue.batchJobs[FUR.bIdxBurner].workpieces[FUR.wIdxBurner];
			// finished
			VG.state := VG_State.TO_FUR_PUT_W_FINISHED;
		END_IF
	6:

END_CASE

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
CASE step OF
	1:
		IF NOT(SL.currW.format.ejectEnabled) THEN
			// set flag to inform HR that VG is taking a w to bring it back to HR
			VG.storeW := TRUE;
			//VG.bIdx := SL.bIdx;
			//VG.wIdx := SL.wIdx;
			//VG.currW := TC.batchJobQueue.batchJobs[VG.bIdx].workpieces[VG.wIdx];
		END_IF
		// select sorting line pos
		IF 	TC.batchJobQueue.batchJobs[SL.bIdx].workpieces[SL.wIdx].format.colorCheckEnabled THEN
			IF TC.batchJobQueue.batchJobs[SL.bIdx].workpieces[SL.wIdx].color = 'white' AND NOT(SL.lbWhite) THEN
				sorting_line_pos := 1;
			ELSIF TC.batchJobQueue.batchJobs[SL.bIdx].workpieces[SL.wIdx].color = 'red' AND NOT(SL.lbRed) THEN
				sorting_line_pos := 2;
			ELSIF TC.batchJobQueue.batchJobs[SL.bIdx].workpieces[SL.wIdx].color = 'blue' AND NOT(SL.lbBlue) THEN
				sorting_line_pos := 3;
			END_IF
		ELSE
			// when color was not checked
			sorting_line_pos := 1;
		END_IF
		step := step + 1;
	2:
		// x and z axis: move to SL
		IF NOT(fin) THEN
			CASE sorting_line_pos OF
				1:
					vg_move_to_pos(x := 58, y := 0, z := 69);
				2:
					vg_move_to_pos(x := 63, y := 0, z := 57);
				3:
					vg_move_to_pos(x := 87, y := 0, z := 47);
			END_CASE
		fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	3:
		// y axis: move to SL
		IF NOT(fin) THEN
			CASE sorting_line_pos OF
				1:
					vg_move_to_pos(x := 58, y := 125, z := 69);
				2:
					vg_move_to_pos(x := 63, y := 125, z := 57);
				3:
					vg_move_to_pos(x := 87, y := 125, z := 47);
			END_CASE
		fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	4:
		// succ workpiece at SL
		VG.valve := TRUE;
		timer(IN:= TRUE, PT:= T#2S);
		IF timer.Q = FALSE AND timer.IN THEN
			// go
			VG.compressor := TRUE;
		ELSE
			// stop
			VG.compressor := FALSE;
			// reset timer
			timer(IN:= FALSE);
			step := step + 1;
		END_IF
	5:
		// y axis: go up as far as possible
		IF NOT(VG.sVertical) THEN
			VG.mVerticalUp := TRUE;
		ELSE
			VG.mVerticalUp := FALSE;
			// reset y axis counter
			VG.cY := 0;
			// reset step counter
			step := 1;
			// reset sorting line pos
			sorting_line_pos := -1;
			// finished
			VG.state := VG_State.TO_SL_TAKE_W_FINISHED;
		END_IF
END_CASE

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
