all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
CASE step OF
	1:
		// x and z axis: move to HR
		IF NOT(fin) THEN
			vg_move_to_pos(x := 27, y := 0, z := 206);
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	2:
		// box without workpiece arrived, wait 0.5 sec to be sure
		IF NOT(HR.lbOut) AND HR.state = HR_State.WAIT_FOR_VG_TO_BRING_W THEN
			timer(IN:= TRUE, PT:= T#0.5S);
			IF NOT(timer.Q = FALSE AND timer.IN) THEN
				timer(IN:= FALSE);
				step := step + 1;
			END_IF
		END_IF
	3:
		// y axis: move down at HR
		IF NOT(fin) THEN
			vg_move_to_pos(x:=27, y:=40, z:=206);
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	4:
		// open ventil
		VG.valve := FALSE;
		step := step + 1;
	5:
		timer(IN:= TRUE, PT:= T#1S);
		IF NOT(timer.Q = FALSE AND timer.IN) THEN
			// reset timer
			timer(IN:= FALSE);
			// go to next step
			step := step + 1;
		END_IF
	6:
		// y axis: go up as far as possible
		IF NOT(VG.sVertical) THEN
			VG.mVerticalUp := TRUE;
		ELSE
			VG.mVerticalUp := FALSE;
			// reset y axis counter
			VG.cY := 0;
			// reset step counter
			step := 1;
			// finished
			VG.state := VG_State.TO_HR_PUT_W_FINISHED;
		END_IF
END_CASE

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
CASE step OF
	1:
		// x and z axis: move to HR
		IF NOT(fin) THEN
			vg_move_to_pos(x := 27, y := 0, z := 206);
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	2:
		IF NOT(HR.lbOut) THEN
			// box with workpiece arrived, wait 0.5 sec to be sure
			timer(IN:= TRUE, PT:= T#0.5S);
			IF NOT(timer.Q = FALSE AND timer.IN) THEN
				timer(IN:= FALSE);
				step := step + 1;
			END_IF		
		END_IF
	3:
		// y axis: move down at HR
		IF NOT(fin) THEN
			vg_move_to_pos(x:=27, y:=40, z:=206);
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	4:
		// succ w at HR
		VG.valve := TRUE;
		timer(IN:= TRUE, PT:= T#2S);
		IF timer.Q = FALSE AND timer.IN THEN
			// go
			VG.compressor := TRUE;
		ELSE
			// stop
			VG.compressor := FALSE;
			// reset timer
			timer(IN:= FALSE);
			step := step + 1;
		END_IF	
	5:
		// y axis: go up as far as possible
		IF NOT(VG.sVertical) THEN
			VG.mVerticalUp := TRUE;
		ELSE
			VG.mVerticalUp := FALSE;
			// reset y axis counter
			VG.cY := 0;
			// reset step counter
			step := 1;
			// finished
			VG.state := VG_State.TO_HR_TAKE_W_FINISHED;
		END_IF
END_CASE

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
CASE step OF
	1:
		// x and z axis: move to EJECT
		IF NOT(fin) THEN
			IF VG.currW.color = 'white' THEN
				// go to eject point 1
				vg_move_to_pos(x := 25, y := 0, z := 2);
			ELSIF VG.currW.color = 'red' THEN
				// go to eject point 2
				vg_move_to_pos(x := 30, y := 0, z := 15);
			ELSIF VG.currW.color = 'blue' THEN
				// go to eject point 3
				vg_move_to_pos(x := 49, y := 0, z := 23);
			ELSE
				// if color 'unknown' then eject to white eject point
				// go to eject point 1
				vg_move_to_pos(x := 25, y := 0, z := 2);
			END_IF
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	2:
		// x and z axis: move to EJECT
		IF NOT(fin) THEN
			IF VG.currW.color = 'white' THEN
				// go to eject point 1
				vg_move_to_pos(x := 25, y := 133, z := 2);
			ELSIF VG.currW.color = 'red' THEN
				// go to eject point 2
				vg_move_to_pos(x := 30, y := 133, z := 15);
			ELSIF VG.currW.color = 'blue' THEN
				// go to eject point 3
				vg_move_to_pos(x := 49, y := 133, z := 23);
			ELSE
				// if color 'unknown' then eject to white eject point
				// go to eject point 1
				vg_move_to_pos(x := 25, y := 133, z := 2);
			END_IF
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	3:
		// open valve to drop w
		VG.valve := FALSE;
		timer(IN:= TRUE, PT:= T#1S);
		IF NOT(timer.Q = FALSE AND timer.IN) THEN
			// reset timer
			timer(IN:= FALSE);
			// reset step counter
			step := 1;
			// finished
			VG.state := VG_State.TO_EJECT_FINISHED;
		END_IF
END_CASE

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
IF NOT(fin) THEN
	IF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx > TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].batchSize THEN
		IF SM.currW.state = 0 AND HR.bIdx = 0 AND VG.bIdx = 0 AND FUR.bIdxBurner = 0 AND FUR.bIdxSuccer = 0 AND FUR.bIdxSaw = 0 AND SL.bIdx = 0 THEN
			// there is no new workpiece in current BatchJob and all maschines finished processing --> BatchJob finished
			TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].state := BatchJobState.FINISHED;
			// delete finished BatchJob
			TC.batchJobQueue.batchJobs[TC.currBatchJobIdx] := TC.nullB;
			// update counter of BatchJobs in queue
			TC.batchJobQueue.amountBatchJobs := TC.batchJobQueue.amountBatchJobs - 1;
			// start next batch job if there is one
			TC.currBatchJobIdx := TC.currBatchJobIdx + 1;
			IF TC.currBatchJobIdx > 20 THEN
				TC.currBatchJobIdx := 1;
			END_IF
			fin := TRUE;
		END_IF
	ELSE
		// batch job not finished --> execute steps for next workpiece
		fin := TRUE;
	END_IF
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
IF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].state = BatchJobState.NULL THEN
	// no batchjob available
	// wait until new batchjob is loaded
ELSIF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].mode = ExecutionMode.single THEN
	SM.currW := TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx];
	IF SM.currW.format.ejectEnabled AND NOT(SM.currW.format.furEnabled) AND NOT(SM.currW.format.sawEnabled) AND NOT(SM.currW.format.colorCheckEnabled) THEN
		// dont set SL flags because workpiece will directly be ejected HR -> VG -> EJECT
		SL.bIdx := 0;
		SL.wIdx := 0;
	ELSE
		// set SL pointers
		SL.bIdx := TC.currBatchJobIdx;
		SL.wIdx := TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx;
	END_IF
	TC.state := TC_State.W_LOADED;
ELSIF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].mode = ExecutionMode.multi AND (HR.state = HR_State.READY OR HR.state = HR_State.FINISHED_SINGLE_MODE OR HR.state = HR_State.SORTING) THEN
	// new w loaded in TC, load it to HR.currW
	Hr.bIdx := TC.currBatchJobIdx;
	Hr.wIdx := TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx;
	HR.currW := TC.batchJobQueue.batchJobs[Hr.bIdx].workpieces[Hr.wIdx];
	TC.state := TC_State.W_LOADED;
ELSIF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].mode = ExecutionMode.sorting THEN
	TC.state := TC_State.W_LOADED;
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
IF started THEN
	IF NOT(fin) THEN
		IF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].state <> BatchJobState.NULL THEN
			IF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].mode = ExecutionMode.single THEN
				// start single mode
				TC.mode_active := ExecutionMode.single;
				TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].state := BatchJobState.STARTED;
				fin := TRUE;
			ELSIF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].mode = ExecutionMode.multi THEN
				// start multi mode
				TC.mode_active := ExecutionMode.multi;
				TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].state := BatchJobState.STARTED;
				fin := TRUE;
			ELSIF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].mode = ExecutionMode.sorting THEN
				// start sorting mode
				TC.mode_active := ExecutionMode.sorting;
				TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].state := BatchJobState.STARTED;
				fin := TRUE;
			END_IF
		ELSE
			// currently no BatchJob available
			// reset HR pointers, cause they may still set
			HR.bIdx := 0;
			HR.wIdx := 0;
			HR.currW := TC.nullW;
		END_IF
	END_IF
END_IF

fin := FALSE;

// X AXIS
IF x < 0 THEN
	// drive right
	IF cX = x THEN
		// if counter reached demanded position, we're done
		HR.mHorizontalRight := FALSE;
		HR.mHorizontalLeft := FALSE;
		xFin := TRUE;
	ELSE
		xFin := FALSE;
		IF cX > x THEN
			HR.mHorizontalRight := TRUE;
			IF HR.encHorizontal AND NOT(xAlreadyCounted) THEN
				// update local counter
				cX := cX - 1;
				// update global counter
				HR.cX := HR.cX - 1;
				xAlreadyCounted := TRUE;
			ELSIF NOT(HR.encHorizontal) THEN
				xAlreadyCounted := FALSE;
			END_IF
		END_IF
	END_IF
ELSE
	// x >= 0
	// drive left
	IF cX = x THEN
		// if counter reached demanded position, we're done
		HR.mHorizontalRight := FALSE;
		HR.mHorizontalLeft := FALSE;
		xFin := TRUE;
	ELSE
		xFin := FALSE;
		IF cX < x THEN
			HR.mHorizontalLeft := TRUE;
			IF HR.encHorizontal AND NOT(xAlreadyCounted) THEN
				// update local counter
				cX := cX + 1;
				// update global counter
				HR.cX := HR.cX + 1;
				xAlreadyCounted := TRUE;
			ELSIF NOT(HR.encHorizontal) THEN
				xAlreadyCounted := FALSE;
			END_IF
		END_IF
	END_IF
END_IF

// Y AXIS
IF y < 0 THEN
	// drive up
	IF cY = y THEN
		// if counter reached demanded position, we're done
		HR.mVerticalUp := FALSE;
		HR.mVerticalDown := FALSE;
		yFin := TRUE;
	ELSE
		yFin := FALSE;
		IF cY > y THEN
			HR.mVerticalUp := TRUE;
			IF HR.encVertical AND NOT(yAlreadyCounted) THEN
				// update local counter
				cY := cY - 1;
				// update global counter
				HR.cY := HR.cY - 1;
				yAlreadyCounted := TRUE;
			ELSIF NOT(HR.encVertical) THEN
				yAlreadyCounted := FALSE;
			END_IF
		END_IF
	END_IF
ELSE
	// y >= 0
	// drive down
	IF cY = y THEN
		// if counter reached demanded position, we're done
		HR.mVerticalDown := FALSE;
		HR.mVerticalUp := FALSE;
	yFin := TRUE;
	ELSE
		yFin := FALSE;
		IF cY < y THEN
			HR.mVerticalDown := TRUE;
			IF HR.encVertical AND NOT(yAlreadyCounted) THEN
				// update local counter
				cY := cY + 1;
				// update global counter
				HR.cY := HR.cY + 1;
				yAlreadyCounted := TRUE;
			ELSIF NOT(HR.encVertical) THEN
				yAlreadyCounted := FALSE;
			END_IF
		END_IF
	END_IF
END_IF

// if x and y axis are finsihed, we're done
IF xFin AND yFin THEN
	// reset local counters
	cX := 0;
	cY := 0;
	xFin := FALSE;
	yFin := FALSE;
	fin := TRUE;
	RETURN;
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
IF FUR.currWBurner.state <> W_State.NULL THEN
	// fur burner got an w
	IF FUR.currWBurner.format.furEnabled AND NOT(finBurner) THEN
		// burn
		fur_burn(w := FUR.currWBurner);
		finBurner := fur_burn.fin;
	ELSE
		IF FUR.currWSuccer.state = W_State.NULL THEN
			// bend pointers of fur succer to current workpiece
			counter := counter + 1;

			FUR.bIdxSuccer := FUR.bIdxBurner;
			FUR.wIdxSuccer := FUR.wIdxBurner;
			FUR.currWSuccer := TC.batchJobQueue.batchJobs[FUR.bIdxSuccer].workpieces[FUR.wIdxSuccer];
			// reset own pointers
			FUR.bIdxBurner := 0;
			FUR.wIdxBurner := 0;
			FUR.currWBurner := TC.nullW;
			finBurner := FALSE;
		END_IF
	END_IF
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
CASE stepSuccer OF
	1:	
		IF FUR.currWBurner.state <> W_State.NULL OR FUR.currWSuccer.state <> W_State.NULL THEN
			stepSuccer := stepSuccer + 1;
		ELSE
			// do nothing, because w will not be sawed or color checked, so VG will bring w back to HR or to EJECT after burning
		END_IF
	2:
		// fur succer got an w --> pick it up and bring it to saw
		// bring succer to furnace
		IF NOT(FUR.sSuckerPosFF) THEN
			FUR.mSuckertoFurnance := TRUE;
		ELSE
			FUR.mSuckertoFurnance := FALSE;
			stepSuccer := stepSuccer + 1;
		END_IF
	3:
		// succer at waggon and rotation desk at succer pos, bring succer down to pick up w
		IF FUR.sFurnanceSliderOutside AND FUR.sRaPosSucker THEN
			FUR.compressor := TRUE;
			FUR.valveLowering := TRUE;
			timer(IN:= TRUE, PT := T#2S);
			IF NOT(timer.Q = FALSE AND timer.IN) THEN
				// reset timer
				timer(IN:= FALSE);
				// go to next step
				//FUR.state := FUR_State.FUR_SUCCER_DOWN_AT_FUR;
				stepSuccer := stepSuccer + 1;
			END_IF
		END_IF
	4: 
		// succer at waggon, succed w, pick up w
		FUR.valveLowering := FALSE;
		FUR.valveVacuum := TRUE; // otherwise vacuum will not be created
		//SM.currW.state := StateEnum.AT_FUR_ATTACHED_TO_SUCCER;
		//FUR.state := FUR_State.FUR_SUCCER_UP_AT_FUR;
		stepSuccer := stepSuccer + 1;
	5:
		// succer to rotation desk
		IF  NOT(FUR.sSuckerPosRa) THEN
			FUR.mSuckertoRa := TRUE;
			//FUR.state := FUR_State.FUR_SUCCER_TO_ROTARY_DESK;
		ELSE
			FUR.mSuckertoRa := FALSE;
			FUR.compressor := FALSE;
			FUR.valveLowering := TRUE;
			//FUR.state := FUR_State.FUR_SUCCER_AT_ROTARY_DESK;
			stepSuccer := stepSuccer + 1;
		END_IF
	6:
		// succer at rotation desk, bring succer down in order to put down w
		IF FUR.sFurnanceSliderOutside THEN
			FUR.compressor := TRUE;
			FUR.valveLowering := TRUE;
			FUR.valveVacuum := FALSE;
			timer(IN:= TRUE, PT := T#1S);
			IF NOT(timer.Q = FALSE AND timer.IN) THEN
				timer(IN:= FALSE);
				FUR.valveLowering := FALSE;
				//FUR.state := FUR_State.FUR_SUCCER_DOWN_AT_RD;
				stepSuccer := stepSuccer + 1;
			END_IF
		END_IF
	7:
		// wait 200ms before rotating desk
		timer(IN:= TRUE, PT := T#200MS);
		IF NOT(timer.Q = FALSE AND timer.IN) THEN
			timer(IN:= FALSE);
			//FUR.state := FUR_State.FUR_SUCCER_UP_AT_RD;
			//SM.currW.state := W_State.AT_FUR_ON_ROTARY_DESK;
			stepSuccer := stepSuccer + 1;
		END_IF
	8:
		IF FUR.currWSaw.state = W_State.NULL THEN
			// bend pointers of saw to current workpiece
			FUR.bIdxSaw := FUR.bIdxSuccer;
			FUR.wIdxSaw := FUR.wIdxSuccer;
			FUR.currWSaw := TC.batchJobQueue.batchJobs[FUR.bIdxSaw].workpieces[FUR.wIdxSaw];
			// reset own pointers
			FUR.bIdxSuccer := 0;
			FUR.wIdxSuccer := 0;
			FUR.currWSuccer := TC.nullW;
			// finsihed, go back to 1 to wait for next w
			stepSuccer := 1;
		ELSE
			// wait for saw to be ready to take a new w
		END_IF

END_CASE

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
CASE stepSaw OF
	1:	
		IF FUR.currWSaw.state <> W_State.NULL THEN
			IF FUR.currWSaw.format.sawEnabled THEN
				// bring w to saw
				//SM.currW.state := StateEnum.AT_FUR_AT_SAW;
				IF NOT(FUR.sRaPosSaw) THEN
					FUR.mRaClockwise := TRUE;
				ELSE
					FUR.mRaClockwise := FALSE;
					stepSaw := stepSaw + 1;
				END_IF
			ELSE
				// skip saw and bring w directly to SL
				stepSaw := stepSaw + 2;
			END_IF
		END_IF
	2:
		// saw
		timer(IN:= TRUE, PT := FUR.currWSaw.format.sawDuration);
		FUR.mSaw := TRUE;
		IF NOT(timer.Q = FALSE AND timer.IN) THEN
			timer(IN:=FALSE);
			FUR.mSaw := FALSE;
			//FUR.state := FUR_State.FUR_SAW_FINISHED;
			stepSaw := stepSaw + 1;
		END_IF
	3: 
		IF SL.currW.state = W_State.NULL THEN
			// bring w to SL
			// SM.currW.state := StateEnum.AT_FUR_ON_CONVEYOR;
			// bend pointers of SL to current workpiece
			SL.bIdx := FUR.bIdxSaw;
			SL.wIdx := FUR.wIdxSaw;
			SL.currW := TC.batchJobQueue.batchJobs[SL.bIdx].workpieces[SL.wIdx];
			// reset own pointers
			FUR.bIdxSaw := 0;
			FUR.wIdxSaw := 0;
			FUR.currWSaw := TC.nullW;

			stepSaw := stepSaw + 1;
		ELSE
			// wait for SL to be ready to take a new w
		END_IF

	4:
		// bring w to SL
		IF NOT(FUR.sRaPosConveyorBelt) THEN
			FUR.mRaClockwise := TRUE;
		ELSE
			FUR.mRaClockwise := FALSE;
			FUR.compressor := TRUE;
			FUR.valveSlider := TRUE;
		END_IF

		IF SL.lbEntry THEN
			FUR.mConveyorBeltForward := TRUE;
		ELSE
			FUR.mConveyorBeltForward := FALSE;
			FUR.compressor := FALSE;
			FUR.valveSlider := FALSE;
			//FUR.state := FUR_State.FUR_W_FINISHED_BACK_TO_DEFAULT;
			stepSaw := stepSaw + 1;
		END_IF
	5:
		// get rotating desk back to succer pos
		IF NOT(FUR.sRaPosSucker) THEN
			FUR.mRaCClockwise := TRUE;
		ELSE
			FUR.mRaCClockwise := FALSE;
			// finsihed, go back to 1 to wait for next w
			stepSaw := 1;
		END_IF
END_CASE

CASE step OF
	1:	// to conveyor if not already there
		fin := FALSE;
		hr_move_to_pos(pos := RackPos.CONVEYOR, upOrDown := RackPos.DOWN);
		stepFin := hr_move_to_pos.fin;
		IF stepFin THEN
			step := step + 1;
			stepFin := FALSE;
		END_IF
	2:	// take w from conveyor
		hr_take_w_from_conveyor(w := w);
		stepFin := hr_take_w_from_conveyor.fin;
		IF stepFin THEN
			step := step + 1;
			stepFin := FALSE;
		END_IF
	3:	// to rack
		hr_move_to_pos(pos := w.endPos, upOrDown := RackPos.UP);
		stepFin := hr_move_to_pos.fin;
		IF stepFin THEN
			step := step + 1;
			stepFin := FALSE;
		END_IF
	4:	// put w into rack
		hr_put_w();
		stepFin := hr_put_w.fin;
		IF stepFin THEN
			step := step + 1;
			stepFin := FALSE;
		END_IF
	5:	// finished
		fin := TRUE;
		step := 1;
END_CASE

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
IF pneumatic_loaded = FALSE THEN
	VG.valve := TRUE;
	// init timer
	timer(IN:= TRUE, PT:= T#2S);

	IF timer.Q = FALSE AND timer.IN THEN
		// go
		VG.compressor := TRUE;
	ELSE
		// stop
		VG.compressor := FALSE;

		// reset timer
		timer(IN:= FALSE);

		// go to next step
		pneumatic_loaded := TRUE;
	END_IF
ELSE
	// reset timer
	timer(IN:= FALSE);
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
CASE step OF
	1:
		// decide to which positon of SL VG will go
		IF NOT(SL.lbWhite) THEN
			// go to sorting line pos 1: 
			VG.state := VG_State.TO_SL_OUTPUT_1;
			step := step + 1;
		ELSIF NOT(SL.lbRed) THEN
			// go to sorting line pos 2: 
			VG.state := VG_State.TO_SL_OUTPUT_2;
			step := step + 1;
		ELSIF NOT(SL.lbBlue) THEN
			// go to sorting line pos 3: 
			VG.state := VG_State.TO_SL_OUTPUT_3;
			step := step + 1;
		END_IF
	2:
		// x and z axis: move to SL 
		IF NOT(fin) THEN
			CASE VG.state OF
				VG_State.TO_SL_OUTPUT_1:
					vg_move_to_pos(x := 58, y := 0, z := 69);
				VG_State.TO_SL_OUTPUT_2:
					vg_move_to_pos(x := 63, y := 0, z := 57);
				VG_State.TO_SL_OUTPUT_3:
					vg_move_to_pos(x := 87, y := 0, z := 47);
			END_CASE
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	3:
		// y axis: move to SL
		IF NOT(fin) THEN
			CASE VG.state OF
				VG_State.TO_SL_OUTPUT_1:
					vg_move_to_pos(x := 58, y := 125, z := 69);
					TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx].sortingLinePos := 1;
				VG_State.TO_SL_OUTPUT_2:
					vg_move_to_pos(x := 63, y := 125, z := 57);
					TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx].sortingLinePos := 2;
				VG_State.TO_SL_OUTPUT_3:
					vg_move_to_pos(x := 87, y := 125, z := 47);
					TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx].sortingLinePos := 3;
			END_CASE
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	4:
		CASE VG.state OF
			VG_State.TO_SL_OUTPUT_1:
				VG.state := VG_State.AT_SL_OUTPUT_1;
			VG_State.TO_SL_OUTPUT_2:
				VG.state := VG_State.AT_SL_OUTPUT_2;
			VG_State.TO_SL_OUTPUT_3:
				VG.state := VG_State.AT_SL_OUTPUT_3;
		END_CASE
		// succ workpiece
		IF pneumatic_loaded = FALSE THEN
			VG.valve := TRUE;
			// init timer
			timer(IN:= TRUE, PT:= T#2S);
			IF timer.Q = FALSE AND timer.IN THEN
				// go
				VG.compressor := TRUE;
			ELSE
				// stop
				VG.compressor := FALSE;
				// reset timer
				timer(IN:= FALSE);
				// set flag
				pneumatic_loaded := TRUE;
			END_IF
		ELSE
			step := step + 1;
		END_IF
	5:
		// y axis: move up
		IF NOT(VG.sVertical) THEN
			VG.mVerticalUp := TRUE;
		ELSE
			VG.mVerticalUp := FALSE;
			VG.cY := 0;
			step := step + 1;
		END_IF
	6:
		// finsihed
		VG.state := VG_State.ABOVE_SL;
END_CASE

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
// Brings VG to its default pos where sVertical = sHorizontal = sRotation = 1
// 1. y-Axis 2. z-Axis 3. rotation
IF NOT(VG.sVertical) THEN
	VG.mVerticalUp := TRUE;
ELSE
	VG.mVerticalUp := FALSE;
	VG.cY := 0;
END_IF
IF NOT(VG.sHorizontal) THEN
	VG.mHorizontalBackward := TRUE;
ELSE
	VG.mHorizontalBackward := FALSE;
	VG.cX := 0;
END_IF
IF NOT(VG.sRotation) THEN
	VG.mRotationClockwise := TRUE;
ELSE
	VG.mRotationClockwise := FALSE;
	VG.cZ := 0;
END_IF

IF VG.sHorizontal AND VG.sVertical AND VG.sRotation THEN
	// default pos reached
	VG.state := VG_State.AT_DEFAULT_POS;
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
// reset flag
yFin := FALSE;
// workpiece is now at VG
SM.currW.linePos := 1;
// reset flag
VG_SINGLE_MODE.subBlockStart := FALSE;

// set HR state
HR.state := HR_State.AT_CONVEYOR_BOX_EMPTY;

IF SM.currW.format.ejectEnabled AND NOT(SM.currW.format.furEnabled) AND NOT(SM.currW.format.sawEnabled) AND NOT(SM.currW.format.colorCheckEnabled) THEN
	// directly eject workpiece
	VG.state := VG_State.FROM_HR_TO_EJECT;
ELSE
	// bring workpiece to FUR
	VG.state := VG_State.FROM_HR_TO_FUR;
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
// Brings VG to its default pos where sVertical = sHorizontal = sRotation = 1
// 1. y-Axis 2. z-Axis 3. rotation
(*IF NOT(VG.sVertical) THEN
	VG.mVerticalUp := TRUE;
ELSE
	VG.mVerticalUp := FALSE;
	IF NOT(VG.sHorizontal) THEN
		VG.mHorizontalBackward := TRUE;
	ELSE
		VG.mHorizontalBackward := FALSE;
		IF NOT(VG.sRotation) THEN
			VG.mRotationClockwise := TRUE;
		ELSE
			VG.mRotationClockwise := FALSE;
			// default pos reached
			VG.state := VG_State.AT_DEFAULT_POS;
			VG.cX := 0;
			VG.cY := 0;
			VG.cZ := 0;
		END_IF
	END_IF
END_IF *)
VG.state := VG_State.AT_DEFAULT_POS;

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
// Brings VG to its default pos where sVertical = sHorizontal = sRotation = 1
// 1. y-Axis 2. z-Axis 3. rotation
IF NOT(VG.sVertical) THEN
	VG.mVerticalUp := TRUE;
ELSE
	VG.mVerticalUp := FALSE;
	IF NOT(VG.sHorizontal) THEN
		VG.mHorizontalBackward := TRUE;
	ELSE
		VG.mHorizontalBackward := FALSE;
		IF NOT(VG.sRotation) THEN
			VG.mRotationClockwise := TRUE;
		ELSE
			VG.mRotationClockwise := FALSE;
			// default pos reached
			VG.state := VG_State.AT_DEFAULT_POS;
			VG.cX := 0;
			VG.cY := 0;
			VG.cZ := 0;
		END_IF
	END_IF
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
CASE step OF
	1:
		// z axis: from HR to EJECT
		// decide to which eject point to go by color
		IF NOT(fin) THEN
			IF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx].color = 'white' THEN
				// go to eject point 1
				vg_move_to_pos(x := 0, y := 0, z := 2);
			ELSIF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx].color = 'red' THEN
				// go to eject point 2
				vg_move_to_pos(x := 0, y := 0, z := 15);
			ELSIF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx].color = 'blue' THEN
				// go to eject point 3
				vg_move_to_pos(x := 0, y := 0, z := 23);
			ELSE
				// if color is unknown, go to eject point 1
				vg_move_to_pos(x := 0, y := 0, z := 2);
			END_IF
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	2:
		// x and y axis: go to eject point
		IF NOT(fin) THEN
			IF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx].color = 'white' THEN
				// go to eject point 1
				vg_move_to_pos(x := 25, y := 133, z := 2);
				VG.state := VG_State.AT_EJECT_1;
			ELSIF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx].color = 'red' THEN
				// go to eject point 2
				vg_move_to_pos(x := 30, y := 133, z := 15);
				VG.state := VG_State.AT_EJECT_2;
			ELSIF TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx].color = 'blue' THEN
				// go to eject point 3
				vg_move_to_pos(x := 49, y := 133, z := 23);
				VG.state := VG_State.AT_EJECT_3;
			ELSE
				// if color is unknown, go to eject point 1
				vg_move_to_pos(x := 25, y := 133, z := 2);
				VG.state := VG_State.AT_EJECT_1;
			END_IF
			fin := vg_move_to_pos.fin;
		ELSE
			fin := FALSE;
			step := step + 1;
		END_IF
	3:
		// open valve to drop workpiece
		VG.valve := FALSE;
		pneumatic_loaded := FALSE;
		timer(IN:= TRUE, PT:= T#1S);
		IF NOT(timer.Q = FALSE AND timer.IN) THEN
			// reset timer
			timer(IN:= FALSE);
			step := step + 1;
		END_IF
	4:
		// Brings VG to its default pos
		IF NOT(VG.sVertical) THEN
			VG.mVerticalUp := TRUE;
		ELSE
			VG.mVerticalUp := FALSE;
			VG.cY := 0;
		END_IF
		IF NOT(VG.sHorizontal) THEN
			VG.mHorizontalBackward := TRUE;
		ELSE
			VG.mHorizontalBackward := FALSE;
			VG.cX := 0;
		END_IF
		IF NOT(VG.sRotation) THEN
			VG.mRotationClockwise := TRUE;
		ELSE
			VG.mRotationClockwise := FALSE;
			VG.cZ := 0;
		END_IF

		IF VG.sHorizontal AND VG.sVertical AND VG.sRotation THEN
			// default pos reached
			VG.state := VG_State.AT_DEFAULT_POS;
			// finished
			step := 1;
		END_IF
END_CASE

fin := FALSE;

// X AXIS (Forward/Backward)
IF x < 0 THEN
	// drive backward
	IF cX = x THEN
		// if counter reached demanded position, we're done
		VG.mHorizontalBackward := FALSE;
		xFin := TRUE;
	ELSE
		xFin := FALSE;
		IF cX > x THEN
			VG.mHorizontalBackward := TRUE;
			IF VG.encHorizontal AND NOT(xAlreadyCounted) THEN
				// update local counter
				cX := cX - 1;
				// update global counter
				VG.cX := VG.cX - 1;
				xAlreadyCounted := TRUE;
			ELSIF NOT(VG.encHorizontal) THEN
				xAlreadyCounted := FALSE;
			END_IF
		END_IF
	END_IF
ELSE
	// x >= 0
	// drive forward
	IF cX = x THEN
		// if counter reached demanded position, we're done
		VG.mHorizontalForward := FALSE;
		xFin := TRUE;
	ELSE
		xFin := FALSE;
		IF cX < x THEN
			VG.mHorizontalForward := TRUE;
			IF VG.encHorizontal AND NOT(xAlreadyCounted) THEN
				// update local counter
				cX := cX + 1;
				// update global counter
				VG.cX := VG.cX + 1;
				xAlreadyCounted := TRUE;
			ELSIF NOT(VG.encHorizontal) THEN
				xAlreadyCounted := FALSE;
			END_IF
		END_IF
	END_IF
END_IF

// Y AXIS (Up/Down)
IF y < 0 THEN
	// drive up
	IF cY = y THEN
		// if counter reached demanded position, we're done
		VG.mVerticalUp := FALSE;
		yFin := TRUE;
	ELSE
		yFin := FALSE;
		IF cY > y THEN
			VG.mVerticalUp := TRUE;
			IF VG.encVertical AND NOT(yAlreadyCounted) THEN
				// update local counter
				cY := cY - 1;
				// update global counter
				VG.cY := VG.cY - 1;
				yAlreadyCounted := TRUE;
			ELSIF NOT(VG.encVertical) THEN
				yAlreadyCounted := FALSE;
			END_IF
		END_IF
	END_IF
ELSE
	// y >= 0
	// drive down
	IF cY = y THEN
		// if counter reached demanded position, we're done
		VG.mVerticalDown := FALSE;
	yFin := TRUE;
	ELSE
		yFin := FALSE;
		IF cY < y THEN
			VG.mVerticalDown := TRUE;
			IF VG.encVertical AND NOT(yAlreadyCounted) THEN
				// update local counter
				cY := cY + 1;
				// update global counter
				VG.cY := VG.cY + 1;
				yAlreadyCounted := TRUE;
			ELSIF NOT(VG.encVertical) THEN
				yAlreadyCounted := FALSE;
			END_IF
		END_IF
	END_IF
END_IF

// Z AXIS (Rotation)
IF z < 0 THEN
	// drive clockwise
	IF cZ = z THEN
		// if counter reached demanded position, we're done
		VG.mRotationClockwise := FALSE;
		zFin := TRUE;
	ELSE
		zFin := FALSE;
		IF cZ > z THEN
			VG.mRotationClockwise := TRUE;
			IF VG.encRotation AND NOT(zAlreadyCounted) THEN
				// update local counter
				cZ := cZ - 1;
				// update global counter
				VG.cZ := VG.cZ - 1;
				zAlreadyCounted := TRUE;
			ELSIF NOT(VG.encRotation) THEN
				zAlreadyCounted := FALSE;
			END_IF
		END_IF
	END_IF
ELSE
	// z >= 0
	// drive counter clockwise
	IF cZ = z THEN
		// if counter reached demanded position, we're done
		VG.mRotationCClockwise := FALSE;
		zFin := TRUE;
	ELSE
		zFin := FALSE;
		IF cZ < z THEN
			VG.mRotationCClockwise := TRUE;
			IF VG.encRotation AND NOT(zAlreadyCounted) THEN
				// update local counter
				cZ := cZ + 1;
				// update global counter
				VG.cZ := VG.cZ + 1;
				zAlreadyCounted := TRUE;
			ELSIF NOT(VG.encRotation) THEN
				zAlreadyCounted := FALSE;
			END_IF
		END_IF
	END_IF
END_IF

// if all axes are finsihed, we're done
IF xFin AND yFin AND zFin THEN
	// reset local counters
	cX := 0;
	cY := 0;
	cZ := 0;
	fin := TRUE;
	RETURN;
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
// increment y calibration counter
IF NOT(calibarteYAxisCounted) THEN
	calibrateYAxis := calibrateYAxis + 1;
	calibarteYAxisCounted := TRUE;
END_IF

IF calibrateYAxis >= 3 THEN
	// calibrate y axis
	IF NOT(HR.sVertical) THEN
		HR.mVerticalUp := TRUE;
	ELSE
		Hr.mVerticalUp := FALSE;
		Hr.cY := 0;
		calibrateYAxis := 0;
	END_IF
ELSE
	CASE step OF
		1:
			// get w from rack
			IF NOT fin THEN
				hr_get(w:= HR.currW);
				fin := hr_get.fin;
			ELSE
				fin := FALSE;
				step := step + 1;
			END_IF
		2:
			// put w on conveyor
			hr_put_w_on_conveyor(w := HR.currW);
			fin := hr_put_w_on_conveyor.fin;	
			IF fin THEN
				step := step + 1;
				fin := FALSE;
			END_IF
		3:
			// bring it to VG in conveyor
			IF HR.lbOut THEN
				HR.mCbForward := TRUE;
			ELSE
				// start 1 sec timer to drive conveyor 1 sec longer to be sure that box is at outside pos
				timer(IN:= TRUE, PT := T#1S);
				IF NOT(timer.Q = FALSE AND timer.IN) THEN
					timer(IN:= FALSE);
					HR.mCbForward := FALSE;
					step := step + 1; 
				END_IF
			END_IF
		4:
			// finished
			step := 1;
			HR.state := HR_State.WAIT_FOR_VG_TO_PICK_UP_W;
			calibarteYAxisCounted := FALSE;
			// bend VG pointers to current workpiece of HR
			VG.bIdx := TC.currBatchJobIdx;
			VG.wIdx := TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx;
			VG.currW := TC.batchJobQueue.batchJobs[VG.bIdx].workpieces[VG.wIdx];
	END_CASE
END_IF

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
CASE step OF
	1:
		IF NOT fin THEN
			hr_get(w:= HR.currW);
			fin := hr_get.fin;
		ELSE
			step := step + 1;
			fin := FALSE;
		END_IF
	2:
		// put w on conveyor
		hr_put_w_on_conveyor(w := HR.currW);
		fin := hr_put_w_on_conveyor.fin;	
		IF fin THEN
			step := step + 1;
			fin := FALSE;
		END_IF
	3:
		// bring it to VG in conveyor
		IF HR.lbOut THEN
			HR.mCbForward := TRUE;
		ELSE
			// start 1 sec timer to drive conveyor 1 sec longer to be sure that box is at outside pos
			timer(IN:= TRUE, PT := T#1S);
			IF NOT(timer.Q = FALSE AND timer.IN) THEN
				timer(IN:= FALSE);
				HR.mCbForward := FALSE;
				step := step + 1; 
			END_IF
		END_IF
	4:
		// finished
		step := 1;
		HR.state := HR_State.WAIT_FOR_VG_TO_BRING_W;
END_CASE

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)

// if sl positions free or if not free if workpiece will not be ejected 
// AND VG is not storing
// AND HR does not already decided to take a new workpiece
// THEN 
IF ((SL.lbRed AND SL.lbWhite AND SL.lbBlue) OR (SL.currW.format.ejectEnabled AND NOT(SL.currW.state = W_State.NULL))) AND NOT(VG.storeW) AND NOT(decidedToTakeANewW) THEN
	HR.bIdx := 0;
	HR.wIdx := 0;
	HR.currW := TC.nullW;
	HR.decidedToTakeANewW := TRUE;
	VG.storeW := FALSE;
ELSIF HR.decidedToTakeANewW THEN
	// get new w out of rack
	IF TC.state = TC_state.W_LOADED THEN
		// set pointers to make sure that they are set
		Hr.bIdx := TC.currBatchJobIdx;
		Hr.wIdx := TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx;
		HR.currW := TC.batchJobQueue.batchJobs[Hr.bIdx].workpieces[Hr.wIdx];
		// workpiece is loaded, start production
		HR.state := HR_State.GET_W;
		// signalise that production starts
		TC.state := TC_State.WAIT_UNTIL_W_FINISHED;
	ELSE
		// no workpiece loaded, load it now
		TC.state := TC_State.LOAD_NEXT_W;
	END_IF
ELSE
	// prevent misconduct when a workpiece is still lying in one of the three spots at SL, although the application got reset
	IF NOT(VG.bIdx = 0) THEN
		// get empty box out of rack
		// set workpiece to get the right box from rack
		IF NOT(SL.lbWhite) OR NOT(SL.lbRed) OR NOT(SL.lbBlue) THEN
			HR.bIdx := SL.bIdx;
			HR.wIdx := SL.wIdx;
			HR.currW := SL.currW;
		ELSE
			HR.bIdx := VG.bIdx;
			HR.wIdx := VG.wIdx;
			HR.currW := VG.currW;
		END_IF
		HR.state := HR_State.GET_BOX;
		// reset flag
		VG.storeW := FALSE;
		END_IF
END_IF

newFormat.furEnabled := furEnabled;
newFormat.sawEnabled := sawEnabled;
newFormat.colorCheckEnabled := colorCheckEnabled;
newFormat.ejectEnabled := ejectEnabled;
newFormat.furDuration := furDuration;
newFormat.sawDuration := sawDuration;
RETURN;

newBatchJob.batchSize := batchSize;
newBatchJob.workpieces := workpieces;
newBatchJob.mode := mode;

// Search for next free position in workpieces. If there is no free position, set nextFreeWPos to -1
WHILE NOT(placeFound) AND NOT(wsFull) DO
	IF workpieces[i].state = W_State.NULL THEN
		placeFound := TRUE;
		newBatchJob.nextFreeWPos := i;
	ELSIF i > newBatchJob.batchSize THEN
		wsFull := TRUE;
		newBatchJob.nextFreeWPos := -1;
	ELSE
		i := i + 1;
	END_IF
END_WHILE

newBatchJob.nextNotProcessedIdx := 1;
newBatchJob.amountFinishedW := 0;
newBatchJob.duration := T#0S;
newBatchJob.pauseDuration := T#0S;
newBatchJob.state := BatchJobState.WAITING;
// current system time
newBatchJob.timeCreated := sysTime.CDT;
RETURN;

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
CASE step OF
	1:
		// get w from rack
		IF NOT fin THEN
			hr_get_w(w:=TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx]);
			fin := hr_get_w.fin;
		ELSE
			fin := FALSE;
			step := step + 1;

		END_IF
	2:
		// put w on conveyor
		hr_put_w_on_conveyor(w := HR.currW);
		fin := hr_put_w_on_conveyor.fin;	
		IF fin THEN
			step := step + 1;
			fin := FALSE;
		END_IF
	3:
		// bring it to VG in conveyor
		IF HR.lbOut THEN
			HR.mCbForward := TRUE;
		ELSE
			// start 1 sec timer to drive conveyor 1 sec longer to be sure that box is at outside pos
			timer(IN:= TRUE, PT := T#1S);
			IF NOT(timer.Q = FALSE AND timer.IN) THEN
				timer(IN:= FALSE);
				HR.mCbForward := FALSE;
				step := step + 1; 
			END_IF
		END_IF
	4:
		// finished
		step := 1;
		HR.state := HR_State.AT_CONVEYOR_BOX_FULL;
END_CASE

(* Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer Institute of Databases and Information Systems, Ulm University, Germany
Permission is hereby granted, free of charge, to any person obtaining a copy of this software and associated documentation files (the "Software"), to deal in the Software without restriction, including without limitation the rights to use, copy, modify, merge, publish, distribute, sublicense, and/or sell copies of the Software, and to permit persons to whom the Software is furnished to do so, subject to the following conditions:
The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.
THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS IN THE SOFTWARE. *) IF NOT hr_store_w.fin THEN hr_store_w(w:=TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].workpieces[TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].nextNotProcessedIdx]); ELSE HR_SINGLE_MODE.subBlockStart := FALSE;
// set BatchJobQueue to next workpiece
HR.state := HR_State.FINISHED_SINGLE_MODE; END_IF

