FUR.any_motor_running := FUR.compressor AND FUR.mRaClockwise AND FUR.mRaCClockwise AND FUR.mConveyorBeltForward AND FUR.mSaw AND FUR.mFurnanceSliderIn  AND FUR.mFurnanceSliderOut AND FUR.mSuckertoFurnance AND FUR.mSuckertoRa AND FUR.compressor  AND FUR.valveVacuum AND FUR.valveLowering AND FUR.valveFurnanceDoor AND FUR.valveSlider;

IF (TC_POU.started OR FUR.any_motor_running) AND NOT(TC.instant_shutdown) THEN
	IF TC.mode_active = ExecutionMode.single THEN
		FUR_SINGLE_MODE();
	ELSIF TC.mode_active = ExecutionMode.multi THEN
		FUR_MULTI_MODE();
	END_IF
ELSE
	// instant shutdown --> all motors off
	FUR.compressor 				:= FALSE;
	FUR.mRaClockwise 			:= FALSE;
	FUR.mRaCClockwise  			:= FALSE;
	FUR.mConveyorBeltForward	:= FALSE;
	FUR.mSaw  					:= FALSE;
	FUR.mFurnanceSliderIn 		:= FALSE;
	FUR.mFurnanceSliderOut 		:= FALSE;
	FUR.mSuckertoFurnance 		:= FALSE;
	FUR.mSuckertoRa 			:= FALSE;
	FUR.compressor 				:= FALSE;
	FUR.valveVacuum				:= FALSE;
	FUR.valveLowering			:= FALSE;
	FUR.valveFurnanceDoor		:= FALSE;
	FUR.valveSlider 			:= FALSE;
END_IF

