HR.any_motor_running := HR.mCBackward OR HR.mCForward OR HR.mCbBackward OR HR.mCbForward OR HR.mHorizontalLeft OR HR.mHorizontalRight OR HR.mVerticalDown OR HR.mVerticalUp;
// invoke the appropriate HR program that fits to the execution mode of the current batch job
IF (TC.batchJobQueue.batchJobs[TC.currBatchJobIdx].state = BatchJobState.STARTED OR HR.any_motor_running) AND NOT(TC.instant_shutdown) THEN
	IF TC.mode_active = ExecutionMode.single THEN
		HR_SINGLE_MODE();
	ELSIF TC.mode_active = ExecutionMode.multi THEN
		HR_MULTI_MODE();
	ELSIF TC.mode_active = ExecutionMode.sorting THEN
		HR.state := HR_State.SORTING;
		HR_SORTING_MODE();
	END_IF
ELSE
	// instant shutdown --> all motors off
	HR.mCBackward 		:= FALSE;
	HR.mCForward  		:= FALSE;
	HR.mCbBackward 		:= FALSE;
	HR.mCbForward  		:= FALSE;
	HR.mHorizontalLeft 	:= FALSE;
	HR.mHorizontalRight := FALSE;
	HR.mVerticalDown 	:= FALSE;
	HR.mVerticalUp 		:= FALSE;
END_IF

