// init some vars TC.currBatchJobIdx := 1; HR.any_motor_running := HR.mCBackward OR HR.mCForward OR HR.mCbBackward OR HR.mCbForward OR HR.mHorizontalLeft OR HR.mHorizontalRight OR HR.mVerticalDown OR HR.mVerticalUp; VG.any_motor_running := VG.mHorizontalBackward OR VG.mHorizontalForward OR VG.mRotationCClockwise OR VG.mRotationClockwise OR VG.mVerticalDown OR VG.mVerticalUp; FUR.any_motor_running := FUR.compressor AND FUR.mRaClockwise AND FUR.mRaCClockwise AND FUR.mConveyorBeltForward AND FUR.mSaw AND FUR.mFurnanceSliderIn AND FUR.mFurnanceSliderOut AND FUR.mSuckertoFurnance AND FUR.mSuckertoRa AND FUR.compressor AND FUR.valveVacuum AND FUR.valveLowering AND FUR.valveFurnanceDoor AND FUR.valveSlider; SL.any_motor_running := SL.mConveyorBelt AND SL.compressor;

