workpieces[1] := newWorkpiece('unknown', W_State.AT_RACK_22, RackPos.RACK_22, RackPos.RACK_22, format15);
workpieces[2] := newWorkpiece('unknown', W_State.AT_RACK_23, RackPos.RACK_23, RackPos.RACK_23, format16);
workpieces[3] := newWorkpiece('unknown', W_State.AT_RACK_13, RackPos.RACK_13, RackPos.RACK_13, format8);
workpieces[4] := newWorkpiece('unknown', W_State.AT_RACK_21, RackPos.RACK_21, RackPos.RACK_21, format16);
