CASE step OF
	1:
		IF SL.currW.state <> W_State.NULL AND SL.state = SL_State.WAITING_FOR_W THEN
			SL.state := SL_State.W_RECEIVED;
			step := step + 1;
		END_IF
	2:
		IF NOT(fin) THEN
			sl_fb();
			fin := sl_fb.fin;
		ELSE
			// wait for VG to pick up w
			SL.state := SL_State.WAITING_FOR_VG_TO_PICK_UP_W;
			step := step + 1;
		END_IF
	3:
		IF VG.state = VG_State.TO_SL_TAKE_W_FINISHED OR SL.state = SL_State.EJECTED THEN
			// finished
			// reset own pointers
			SL.bIdx := 0;
			SL.wIdx := 0;
			SL.currW := TC.nullW;
			// reset fin
			fin := FALSE;
			SL.state := SL_State.WAITING_FOR_W;
			step := 1;
		END_IF
END_CASE

fin := FALSE;

// choose x and y values dependent on the position
CASE pos OF
	RackPos.RACK_11:
		x := 292;
		IF upOrDown = RackPos.DOWN THEN
			y := 17;
		ELSIF upOrDown = RackPos.UP THEN
			y := 1;
		END_IF
	RackPos.RACK_12:
		x := 203;
		IF upOrDown = RackPos.DOWN THEN
			y := 17;
		ELSIF upOrDown = RackPos.UP THEN
			y := 1;
		END_IF
	RackPos.RACK_13:
		x := 114;
		IF upOrDown = RackPos.DOWN THEN
			y := 17;
		ELSIF upOrDown = RackPos.UP THEN
			y := 1;
		END_IF
	RackPos.RACK_21:
		x := 292;
		IF upOrDown = RackPos.DOWN THEN
			y := 70;
		ELSIF upOrDown = RackPos.UP THEN
			y := 54;
		END_IF
	RackPos.RACK_22:
		x := 203;
		IF upOrDown = RackPos.DOWN THEN
			y := 70;
		ELSIF upOrDown = RackPos.UP THEN
			y := 54;
		END_IF
	RackPos.RACK_23:
		x := 114;
		IF upOrDown = RackPos.DOWN THEN
			y := 70;
		ELSIF upOrDown = RackPos.UP THEN
			y := 54;
		END_IF
	RackPos.RACK_31:
		x := 292;
		IF upOrDown = RackPos.DOWN THEN
			y := 129;
		ELSIF upOrDown = RackPos.UP THEN
			y := 113;
		END_IF
	RackPos.RACK_32:
		x := 203;
		IF upOrDown = RackPos.DOWN THEN
			y := 129;
		ELSIF upOrDown = RackPos.UP THEN
			y := 113;
		END_IF
	RackPos.RACK_33:
		x := 114;
		IF upOrDown = RackPos.DOWN THEN
			y := 129;
		ELSIF upOrDown = RackPos.UP THEN
			y := 113;
		END_IF
	RackPos.CONVEYOR:
		x := 0;
		IF upOrDown = RackPos.DOWN THEN
			y := 112;
		ELSIF upOrDown = RackPos.UP THEN
			y := 96;
		END_IF
END_CASE;

// to handle negative counter values
IF HR.cX < 0 THEN
	HR.cX := 0;
END_IF
IF HR.cY < 0 THEN
	HR.cY := 0;
END_IF

// X AXIS
IF HR.cX = x THEN
	// if counter reached demanded position, we're done
	HR.mHorizontalRight := FALSE;
	HR.mHorizontalLeft := FALSE;
	xFin := TRUE;
ELSE
	// SAVETY FIRST: HR does not move in x direction if cantilever is not driven back
	IF NOT(HR.sCBack) THEN
		HR.mCBackward := TRUE;
	ELSE
		HR.mCBackward := FALSE;
		xFin := FALSE;
		IF HR.cX < x AND NOT(xFin) THEN
			HR.mHorizontalLeft := TRUE;
			HR.mHorizontalRight := FALSE;
			IF HR.encHorizontal AND NOT(xAlreadyCounted) THEN
				HR.cX := HR.cX + 1;
				xAlreadyCounted := TRUE;
			ELSIF NOT(HR.encHorizontal) THEN
				xAlreadyCounted := FALSE;
			END_IF
		ELSIF HR.cX > x AND NOT(xFin) THEN
			HR.mHorizontalRight := TRUE;
			HR.mHorizontalLeft := FALSE;
			IF HR.encHorizontal AND NOT(xAlreadyCounted) THEN
				HR.cX := HR.cX - 1;
				xAlreadyCounted := TRUE;
			ELSIF NOT(HR.encHorizontal) THEN
				xAlreadyCounted := FALSE;
			END_IF
		END_IF
	END_IF
END_IF




// Y AXIS
IF HR.cY = y THEN
	// if counter reached demanded position, we're done
	HR.mVerticalUp := FALSE;
	HR.mVerticalDown := FALSE;
	yFin := TRUE;
ELSE
	yFin := FALSE;
	IF HR.cY < y AND NOT(yFin) THEN
		HR.mVerticalDown := TRUE;
		HR.mVerticalUp := FALSE;
		IF HR.encVertical AND NOT(yAlreadyCounted) THEN
			HR.cY := HR.cY + 1;
			yAlreadyCounted := TRUE;
		ELSIF NOT(HR.encVertical) THEN
			yAlreadyCounted := FALSE;
		END_IF
	ELSIF HR.cY > y AND NOT(yFin) THEN
		HR.mVerticalUp := TRUE;
		HR.mVerticalDown := FALSE;
		IF HR.encVertical AND NOT(yAlreadyCounted) THEN
			HR.cY := HR.cY - 1;
			yAlreadyCounted := TRUE;
		ELSIF NOT(HR.encVertical) THEN
			yAlreadyCounted := FALSE;
		END_IF
	END_IF
END_IF

// if x and y axis are finsihed, we're done
IF xFin AND yFin THEN
	xFin := FALSE;
	yFin := FALSE;
	fin := TRUE;
	RETURN;
END_IF

CASE step OF
	1: 	// to rack
		fin := FALSE;
		hr_move_to_pos(pos := w.startPos, upOrDown := RackPos.DOWN);
		stepFin := hr_move_to_pos.fin; 
		IF stepFin THEN
			step := step + 1;
			stepFin := FALSE;
		END_IF
	2:	// take w from rack
		hr_take_w(w := w);
		stepFin := hr_take_w.fin;
		IF stepFin THEN
			step := step + 1;
			stepFin := FALSE;
		END_IF
	3:	// to conveyor
		hr_move_to_pos(pos := RackPos.CONVEYOR, upOrDown := RackPos.UP);
		stepFin := hr_move_to_pos.fin;	
		IF stepFin THEN
			step := step + 1;
			stepFin := FALSE;
		END_IF

	4:	// finished
		fin := TRUE;
		step := 1;
		stepFin := FALSE;
END_CASE

CASE step OF
	1:	// forward
		fin := FALSE;
		IF NOT(HR.sCFront) THEN
			HR.mCForward := TRUE;
		ELSE
			HR.mCForward := FALSE;
			step := step + 1;
		END_IF
	2:	// down
		hr_move(x := 0 , y := 16);
		stepFin := hr_move.fin;
		IF stepFin THEN
			step := step + 1;
			stepFin := FALSE;
		END_IF
	3:	// back
		IF NOT(HR.sCBack) THEN
			HR.mCBackward := TRUE;
		ELSE
			HR.mCBackward := FALSE;
			step := step + 1;
		END_IF
	4:	// finished
		fin := TRUE;
		step := 1;
END_CASE

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
// get HR to its default position
// needed, if a job multi / single mode batch job will be executed after ths batch
IF HR.sCBack = FALSE THEN
	HR.mCBackward := TRUE;
ELSE 
	HR.mCBackward := FALSE;
	// set tower vertical position to max UP as well as horizontal position max RIGHT
	IF HR.sHorizontal = FALSE THEN
		HR.mHorizontalRight := TRUE;
	ELSE
		HR.mHorizontalRight := FALSE;
		HR.cX := 0;
	END_IF

	IF HR.sVertical = FALSE THEN
		HR.mVerticalUp := TRUE;
	ELSE
		HR.mVerticalUp := FALSE;
		HR.cY := 0;
	END_IF
END_IF

IF HR.sHorizontal AND HR.sVertical AND HR.sCBack THEN
	// finished
	fin := TRUE;
END_IF

fin := FALSE;
CASE step OF
	1:	// forward
		IF NOT(HR.sCFront) THEN
			HR.mCForward := TRUE;
		ELSE
			HR.mCForward := FALSE;
			step := step + 1;
		END_IF
	2:	// up
		hr_move(x := 0, y := -16);
		stepFin := hr_move.fin;
		IF stepFin THEN
			step := step + 1;
			stepFin := FALSE;
		END_IF
	3:	// step 3: back
		IF NOT(HR.sCBack) THEN
			HR.mCBackward := TRUE;
		ELSE
			HR.mCBackward := FALSE;
			step := step + 1;
		END_IF
	4:	// finished
		fin := TRUE;
		step := 1;
		stepFin := FALSE;	
END_CASE

fin := FALSE;
// to handle negative counter values
IF VG.cX < 0 THEN
	VG.cX := 0;
END_IF
IF VG.cY < 0 THEN
	VG.cY := 0;
END_IF
IF VG.cZ < 0 THEN
	VG.cZ := 0;
END_IF

// X AXIS (Forward/Backward)
IF VG.cX = x THEN
	// if counter reached demanded position, we're done
	VG.mHorizontalForward := FALSE;
	VG.mHorizontalBackward := FALSE;
	xFin := TRUE;
ELSE
	xFin := FALSE;
	IF VG.cX < x AND NOT(xFin) THEN
		VG.mHorizontalForward := TRUE;
		VG.mHorizontalBackward := FALSE;
		IF VG.encHorizontal AND NOT(xAlreadyCounted) THEN
			VG.cX := VG.cX + 1;
			xAlreadyCounted := TRUE;
		ELSIF NOT(VG.encHorizontal) THEN
			xAlreadyCounted := FALSE;
		END_IF
	ELSIF VG.cX > x AND NOT(xFin) THEN
		VG.mHorizontalForward := FALSE;
		VG.mHorizontalBackward := TRUE;
		IF VG.encHorizontal AND NOT(xAlreadyCounted) THEN
			VG.cX := VG.cX - 1;
			xAlreadyCounted := TRUE;
		ELSIF NOT(VG.encHorizontal) THEN
			xAlreadyCounted := FALSE;
		END_IF
	END_IF
END_IF

// Y AXIS (Up/Down)
IF VG.cY = y THEN
	// if counter reached demanded position, we're done
	VG.mVerticalUp := FALSE;
	VG.mVerticalDown := FALSE;
	yFin := TRUE;
ELSE
	yFin := FALSE;
	IF VG.cY < y AND NOT(yFin) THEN
		VG.mVerticalDown := TRUE;
		VG.mVerticalUp := FALSE;
		IF VG.encVertical AND NOT(yAlreadyCounted) THEN
			VG.cY := VG.cY + 1;
			yAlreadyCounted := TRUE;
		ELSIF NOT(VG.encVertical) THEN
			yAlreadyCounted := FALSE;
		END_IF
	ELSIF VG.cY > y AND NOT(yFin) THEN
		VG.mVerticalUp := TRUE;
		VG.mVerticalDown := FALSE;
		IF VG.encVertical AND NOT(yAlreadyCounted) THEN
			VG.cY := VG.cY - 1;
			yAlreadyCounted := TRUE;
		ELSIF NOT(VG.encVertical) THEN
			yAlreadyCounted := FALSE;
		END_IF
	END_IF
END_IF

// Z AXIS (Rotation)
IF VG.cZ = z THEN
	// if counter reached demanded position, we're done
	VG.mRotationCClockwise := FALSE;
	VG.mRotationClockwise := FALSE;
	zFin := TRUE;
ELSE
	zFin := FALSE;
	IF VG.cZ < z AND NOT(zFin) THEN
		VG.mRotationCClockwise := TRUE;
		VG.mRotationClockwise := FALSE;
		IF VG.encRotation AND NOT(zAlreadyCounted) THEN
			VG.cZ := VG.cZ + 1;
			zAlreadyCounted := TRUE;
		ELSIF NOT(VG.encRotation) THEN
			zAlreadyCounted := FALSE;
		END_IF
	ELSIF VG.cZ > z AND NOT(zFin) THEN
		VG.mRotationCClockwise := FALSE;
		VG.mRotationClockwise := TRUE;
		IF VG.encRotation AND NOT(zAlreadyCounted) THEN
			VG.cZ := VG.cZ - 1;
			zAlreadyCounted := TRUE;
		ELSIF NOT(VG.encRotation) THEN
			zAlreadyCounted := FALSE;
		END_IF
	END_IF
END_IF

// if x and y axis are finsihed, we're done
IF xFin AND yFin AND zFin THEN
	fin := TRUE;
	RETURN;
END_IF

CASE step OF
	1:	// forward
		fin := FALSE;	
		IF NOT(HR.sCFront) THEN
			HR.mCForward := TRUE;
		ELSE
			HR.mCForward := FALSE;
			step := step + 1;
		END_IF
	2:	// down
		hr_move(x := 0 , y := 26);
		stepFin := hr_move.fin; 
		IF stepFin THEN
			step := step + 1;
			stepFin := FALSE;
		END_IF
	3:	// finished
		fin := TRUE;
		step := 1;
END_CASE

fin := FALSE;
CASE step OF
	1:	// forward if not already there
		IF NOT(HR.sCFront) THEN
			HR.mCForward := TRUE;
		ELSE
			HR.mCForward := FALSE;
			step := step + 1;
		END_IF
	2:	// backwards
		IF HR.lbIn THEN
			HR.mCbBackward := TRUE;
		ELSE
			HR.mCbBackward := FALSE;
			step := step + 1; 
		END_IF
	3:	// up
		hr_move(x := 0, y := -26);
		stepFin := hr_move.fin;
		IF stepFin THEN
			step := step + 1;
			stepFin := FALSE;
		END_IF
	4:	// back
		IF NOT(HR.sCBack) THEN
			HR.mCBackward := TRUE;
		ELSE
			HR.mCBackward := FALSE;
			step := step + 1;
		END_IF
	5:	// finished
		fin := TRUE;
		step := 1;
		stepFin := FALSE;
END_CASE

CASE step OF
	1:	// open door and drive workpiece into furnace
		// reset flag
		fin := FALSE;
		FUR.compressor := TRUE;
		FUR.valveFurnanceDoor := TRUE;
		IF FUR.sFurnanceSliderInside = FALSE THEN
			FUR.mFurnanceSliderIn := TRUE;
		ELSE
			FUR.mFurnanceSliderIn := FALSE;
			FUR.state := FUR_State.FUR_WAGON_INSIDE;
			step := step + 1;
		END_IF
	2:	// burn
		IF FUR.state = FUR_State.FUR_WAGON_INSIDE THEN
			//SM.currW.state := StateEnum.AT_FUR_IN_FUR;
			timer(IN:= TRUE, PT := FUR.currWBurner.format.furDuration);
			FUR.valveFurnanceDoor := FALSE;
			IF NOT(timer.Q = FALSE AND timer.IN) THEN
				// reset timer
				timer(IN:= FALSE);
				FUR.compressor := TRUE;
				FUR.valveFurnanceDoor := TRUE;
				FUR.state := FUR_State.FUR_BURNED;
				step := step + 1;
			END_IF
		END_IF
	3:	// drive slider out
		IF FUR.state = FUR_State.FUR_BURNED THEN
			//SM.currW.state := StateEnum.AT_FUR_IN_WAGON;
			IF FUR.sFurnanceSliderOutside = FALSE THEN
				FUR.mFurnanceSliderOut := TRUE;
			ELSE
				FUR.mFurnanceSliderOut := FALSE;
				FUR.state := FUR_State.FUR_WAGON_OUTSIDE;
				FUR.compressor := FALSE;
				FUR.valveFurnanceDoor := FALSE;
				step := step + 1;
			END_IF
		END_IF
	4:	// finished
		fin := TRUE;
		step := 1;
END_CASE

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
trigger := 0;
puls_temp := 0;
counter_puls := 0;
white := FALSE;
red := FALSE;
blue := FALSE;

color_value := 4000000;

timer(IN:=FALSE);
fin := FALSE;

SL.mConveyorBelt := TRUE;

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
IF color_value > 2000000 AND color_value < 2558000 THEN//weis: 2630000
	TC.batchJobQueue.batchJobs[SL.bIdx].workpieces[SL.wIdx].color := 'white';
	white := TRUE;
	red := FALSE;
	blue := FALSE;
	SL.state := SL_State.W_READY_FOR_SORTING;
END_IF
IF color_value > 2558001 AND color_value < 3000000 THEN//rot: 2801000
	TC.batchJobQueue.batchJobs[SL.bIdx].workpieces[SL.wIdx].color := 'red';
	white := FALSE;
	red := TRUE;
	blue := FALSE;
	SL.state := SL_State.W_READY_FOR_SORTING;
END_IF
IF color_value > 3400000 AND color_value < 3600000 THEN//blau: 3546000
	TC.batchJobQueue.batchJobs[SL.bIdx].workpieces[SL.wIdx].color := 'blue';
	white := FALSE;
	red := FALSE;
	blue := TRUE;
	SL.state := SL_State.W_READY_FOR_SORTING;
END_IF
IF color_value > 3600000 OR color_value < 2000000 THEN//unknown
	TC.batchJobQueue.batchJobs[SL.bIdx].workpieces[SL.wIdx].color := 'unknown';
	white := FALSE;
	red := FALSE;
	blue := FALSE;
	SL.state := SL_State.THROW_OUT; 
END_IF

SL.mConveyorBelt := TRUE;

(*
Copyright (c) 2019 Manuel Goester, Michael Witt, Klaus Kammerer
Institute of Databases and Information Systems, Ulm University, Germany

Permission is hereby granted, free of charge, to any person obtaining a copy 
of this software and associated documentation files (the "Software"), to deal 
in the Software without restriction, including without limitation the rights 
to use, copy, modify, merge, publish, distribute, sublicense, and/or sell 
copies of the Software, and to permit persons to whom the Software is 
furnished to do so, subject to the following conditions:

The above copyright notice and this permission notice shall be included in 
all copies or substantial portions of the Software.

THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR 
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, 
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE 
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER 
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, 
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS 
IN THE SOFTWARE.
*)
SL.mConveyorBelt := TRUE;
IF TC.batchJobQueue.batchJobs[SL.bIdx].workpieces[SL.wIdx].format.colorCheckEnabled THEN
	timer(IN:= TRUE, PT := T#3S);
	IF white AND counter_puls = 35 THEN
		SL.mConveyorBelt := FALSE;
		SL.compressor := TRUE;
		SL.valveFirstEject := TRUE;
	END_IF

	IF red AND counter_puls = 45 THEN
		SL.mConveyorBelt := FALSE;
		SL.compressor := TRUE;
		SL.valveSecEject := TRUE;
	END_IF

	IF blue AND counter_puls = 55 THEN
		SL.mConveyorBelt := FALSE;
		SL.compressor := TRUE;
		SL.valveThirdEject := TRUE;
	END_IF

	IF NOT(timer.Q = FALSE AND timer.IN) THEN
		SL.mConveyorBelt := FALSE;
		SL.compressor := FALSE;	
		SL.valveFirstEject := FALSE;
		SL.valveSecEject := FALSE;
		SL.valveThirdEject := FALSE;

		timer(IN:= FALSE);

		SL.state := SL_State.SORTING;
	END_IF
ELSE
	timer(IN:= TRUE, PT := T#2S);
	IF counter_puls = 37 THEN
		SL.mConveyorBelt := FALSE;
		SL.compressor := TRUE;
		SL.valveFirstEject := TRUE;
	END_IF
	IF NOT(timer.Q = FALSE AND timer.IN) THEN
		SL.compressor := FALSE;	
		SL.valveFirstEject := FALSE;
		SL.valveSecEject := FALSE;
		SL.valveThirdEject := FALSE;

		timer(IN:= FALSE);

		SL.state := SL_State.SORTING;
	END_IF
END_IF

fin := FALSE;
CASE step OF
	1:	// get w1 out of rack and bring it to conveyor
		IF NOT(stepFin) THEN
			hr_get_w(w := w1);
			stepFin := hr_get_w.fin;
		ELSE
			stepFin := FALSE;
			step := step + 1;
		END_IF
	2:	// put down w1 at conveyor
		IF NOT(stepFin) THEN
			hr_put_w();
			stepFin := hr_put_w.fin;
		ELSE
			stepFin := FALSE;
			step := step + 1;
		END_IF
	3:	// move to w2 position in rack
		IF NOT(stepFin) THEN
			hr_move_to_pos(pos := w2.startPos, upOrDown := RackPos.DOWN);	
			stepFin := hr_move_to_pos.fin;
		ELSE
			stepFin := FALSE;
			step := step + 1;
		END_IF
	4:	// pick up w2
		IF NOT(stepFin) THEN
			hr_take_w();
			stepFin := hr_take_w.fin;
		ELSE
			stepFin := FALSE;
			step := step + 1;
		END_IF
	5:	// move to w1 start-position in rack
		IF NOT(stepFin) THEN
			hr_move_to_pos(pos := w1.startPos, upOrDown := RackPos.UP);	
			stepFin := hr_move_to_pos.fin;
		ELSE
			stepFin := FALSE;
			step := step + 1;
		END_IF
	6:	// put down w2 in rack
		IF NOT(stepFin) THEN
			hr_put_w();
			stepFin := hr_put_w.fin;
		ELSE
			stepFin := FALSE;
			step := step + 1;
		END_IF
	7:	// move to conveyor
		IF NOT(stepFin) THEN
			hr_move_to_pos(pos := RackPos.CONVEYOR, upOrDown := RackPos.DOWN);	
			stepFin := hr_move_to_pos.fin;
		ELSE
			stepFin := FALSE;
			step := step + 1;
		END_IF
	8:	// pick up w1 at conveyor
		IF NOT(stepFin) THEN
			hr_take_w();
			stepFin := hr_take_w.fin;
		ELSE
			stepFin := FALSE;
			step := step + 1;
		END_IF
	9:	// move to rack to start-position of w2
		IF NOT(stepFin) THEN
			hr_move_to_pos(pos := w2.startPos, upOrDown := RackPos.UP);	
			stepFin := hr_move_to_pos.fin;
		ELSE
			stepFin := FALSE;
			step := step + 1;
		END_IF
	10:	// put down w1 in rack
		IF NOT(stepFin) THEN
			hr_put_w();
			stepFin := hr_put_w.fin;
		ELSE
			stepFin := FALSE;
			step := step + 1;
		END_IF
	11:	// finished
		fin := TRUE;
		step := 1;
END_CASE
